const Sequelize = require('sequelize'),
      db        = require('db'),
      User      = require('./user'),
      Location  = require('./location');

const Chat = db.define('chat', {
  message:  {
    type     : Sequelize.STRING,
    allowNull: false
  }
});

Chat.belongsTo(User, { as: 'sender' });
Chat.belongsTo(Location, { as: 'location' });

module.exports = Chat;