const Sequelize = require('sequelize'),
      db        = require('db');

const User = db.define('user', {
  username : {
    type: Sequelize.STRING,
    allowNull: false,
    unique: true
  },
  hash     : {
    type: Sequelize.TEXT,
    allowNull: false
  },
  phone    : Sequelize.STRING
});

module.exports = User;